<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');  header('Content-Type: text/html; charset=utf-8');	class User_setting extends CI_Controller	{		public function index()		{			if ( $this->ion_auth->logged_in() ) {				$loggedin = true;				$data['loggedin'] = $loggedin;				$this->load->view('head_view', $data);				$this->load->view('menu_view', $data);				$this->load->view('user_setting_view', $data);				$this->load->view('footer_view', $data);			} else {				$loggedin = false;				redirect('auth/login', 'refresh');			}		}		public function setting_agent() {			echo 'setting_agent';		}		public function setting_prog() {			echo 'setting_prog';		}	}